import React, { useState, useRef } from "react";
import { useNavigate } from "react-router-dom";
import Form from "react-validation/lib/form";
import Input from "react-validation/lib/input";
import CheckButton from "react-validation/lib/button";
import { Container, Row, Col, Card, Button, Alert, Form as BootstrapForm } from 'react-bootstrap';

import AuthService from "../services/authService";

const required = (value) => {
  if (!value) {
    return (
      <div className="text-danger" role="alert">
        This field is required!
      </div>
    );
  }
};

const validEmail = (value) => {
  if (!value) {
    return (
      <div className="text-danger" role="alert">
        This field is required!
      </div>
    );
  }

  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  if (!emailRegex.test(value)) {
    return (
      <div className="text-danger" role="alert">
        This is not a valid email.
      </div>
    );
  }
};

const vusername = (value) => {
  if (!value) {
    return (
      <div className="text-danger" role="alert">
        This field is required!
      </div>
    );
  }

  if (value.length < 3 || value.length > 20) {
    return (
      <div className="text-danger" role="alert">
        The username must be between 3 and 20 characters.
      </div>
    );
  }
};

const vpassword = (value) => {
  if (!value) {
    return (
      <div className="text-danger" role="alert">
        This field is required!
      </div>
    );
  }

  if (value.length < 6 || value.length > 40) {
    return (
      <div className="text-danger" role="alert">
        The password must be between 6 and 40 characters.
      </div>
    );
  }
};

const Register = () => {
  const form = useRef();
  const checkBtn = useRef();
  const navigate = useNavigate();

  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("WAREHOUSE_STAFF");
  const [successful, setSuccessful] = useState(false);
  const [message, setMessage] = useState("");

  const onChangeUsername = (e) => {
    setUsername(e.target.value);
  };

  const onChangeEmail = (e) => {
    setEmail(e.target.value);
  };

  const onChangePassword = (e) => {
    setPassword(e.target.value);
  };

  const onChangeRole = (e) => {
    setRole(e.target.value);
  };

  const handleRegister = (e) => {
    e.preventDefault();

    setMessage("");
    setSuccessful(false);

    form.current.validateAll();

    if (checkBtn.current.context._errors.length === 0) {
      AuthService.register(username, email, password, role).then(
        (response) => {
          setMessage(response.data.message);
          setSuccessful(true);
          setTimeout(() => {
            navigate("/login");
          }, 2000);
        },
        (error) => {
          const resMessage =
            (error.response &&
              error.response.data &&
              error.response.data.message) ||
            error.message ||
            error.toString();

          setMessage(resMessage);
          setSuccessful(false);
        }
      );
    }
  };

  return (
    <Container className="mt-5">
      <Row className="justify-content-center">
        <Col md={6}>
          <Card className="p-4 shadow">
            <Card.Body>
              <h3 className="text-center mb-4">Sign Up</h3>

              <Form onSubmit={handleRegister} ref={form}>
                {!successful && (
                  <div>
                    <div className="form-group mb-3">
                      <label htmlFor="username">Username</label>
                      <Input
                        type="text"
                        className="form-control"
                        name="username"
                        value={username}
                        onChange={onChangeUsername}
                        validations={[required, vusername]}
                      />
                    </div>

                    <div className="form-group mb-3">
                      <label htmlFor="email">Email</label>
                      <Input
                        type="text"
                        className="form-control"
                        name="email"
                        value={email}
                        onChange={onChangeEmail}
                        validations={[required, validEmail]}
                      />
                    </div>

                    <div className="form-group mb-3">
                      <label htmlFor="password">Password</label>
                      <Input
                        type="password"
                        className="form-control"
                        name="password"
                        value={password}
                        onChange={onChangePassword}
                        validations={[required, vpassword]}
                      />
                    </div>

                    <div className="form-group mb-4">
                      <label htmlFor="role">Role</label>
                      <BootstrapForm.Select 
                        name="role" 
                        value={role}
                        onChange={onChangeRole}
                        className="form-control"
                      >
                        <option value="ADMIN">Admin</option>
                        <option value="LOGISTICS_MANAGER">Logistics Manager</option>
                        <option value="WAREHOUSE_STAFF">Warehouse Staff</option>
                        <option value="DELIVERY_DRIVER">Delivery Driver</option>
                      </BootstrapForm.Select>
                    </div>

                    <div className="form-group text-center">
                      <Button variant="primary" type="submit" className="btn-block w-100">
                        Sign Up
                      </Button>
                    </div>
                  </div>
                )}

                {message && (
                  <div className="form-group mt-3">
                    <Alert variant={successful ? "success" : "danger"}>
                      {message}
                    </Alert>
                  </div>
                )}
                <CheckButton style={{ display: "none" }} ref={checkBtn} />
              </Form>

              <div className="mt-3 text-center">
                <p>Already have an account? <a href="/login">Login</a></p>
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default Register;
